$(function(){$("#loginbtn").focus();$(document).keydown(function(A){if(A.keyCode==13){login()}});$("#loginbtn").click(function(){login()})});function login(){$.ajax({type:"post",url:"login.do",data:$("#formlogin").serialize(),async:true,success:function(A){if(A.statusCode==200){if(A.content.department==5){window.location.href="gameTops.jsp?menuID=8"}else{if(A.content.department==1){window.location.href="usercheck.jsp?menuID=1"}else{window.location.href="customerInfo.jsp?menuID=2"}}}else{if(A.statusCode==1){$(".login-error").show();$(".login-error").html("用户名或者密码不正确。")}else{if(A.statusCode==402){$(".login-error").show();$(".login-error").html("您没有登陆权限，请联系系统管理员。")}}}}})};